package operator;

public class IfCondition_example {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
          int var=1;
          //print variable value is greater than 5
          //if condition need to used
          //condition var>5
          
          //if syntax
          
          /*if(condition){
           * if condition is true perform some specific action 
           * }
           */
          if(var>5)
          {
        	  System.out.println("var is greater than 5");
          }
          else
          {
        	  System.out.println("var is not greater than 5");
          }
	}

}
